import { ChangeEvent, useState } from "react";
import "./App.css";
import { Todo } from "./type";

interface ListProps {
  todos: Todo[];
  onComplete: (id: string) => void;
  editingId: string | null;
  setEditingId: React.Dispatch<React.SetStateAction<string | null>>;
  onUpdate: (id: string, newContent: string) => Promise<void>;
}

export default function List({
  todos,
  onComplete,
  editingId,
  setEditingId,
}: ListProps) {
  const [editingContent, setEditingContent] = useState("");
  const handleEditingContent = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditingContent(e.target.value);
  };
  const handleDoubleClick = (todo: Todo) => {
    setEditingId(todo.id);
    setEditingContent(todo.content);
  };
  return (
    <>
      {todos.map((todo) => (
        <div key={todo.id} className="todoList">
          <button onClick={() => onComplete(todo.id)}>complete</button>
          <input
            type="text"
            value={todo.id === editingId ? editingContent : todo.content}
            readOnly={todo.id !== editingId}
            onDoubleClick={() => handleDoubleClick(todo)}
            onChange={handleEditingContent}
          />
          {todo.id === editingId && <button onClick={onUpdate}>保存</button>}
        </div>
      ))}
    </>
  );
}
