import React, { useEffect, useState } from "react";
import List from "./List.tsx";
import Form from "./Form.tsx";
import api from "./axiosClient.js";
import { Todo } from "./type.ts";

async function fetchTodos() {
  try {
    const results = await api.get(`/api/todos`);
    return results.data;
  } catch (error) {
    console.error("Failed to fetch data");
    return [];
  }
}

export default function () {
  const [todos, setTodos] = useState<Todo[]>([]);

  useEffect(() => {
    fetchTodos().then((data) => {
      setTodos(data);
    });
  }, []);

  const submit = async (formData: FormData) => {
    const todo = formData.get("todo");
    if (typeof todo === "string" && todo.trim() !== "") {
      try {
        const res = await api.post(`/api/todos`, { content: todo.trim() });
        const responseData = res.data;
        console.log("データ受け取り");
        console.log("データ", res);
        if (!responseData || !responseData.id || !responseData.content) {
          throw new Error(
            "Invalid response from server: Missing ID or content."
          );
        }

        const newTodo: Todo = {
          id: String(responseData.id),
          content: responseData.content,
        };
        console.log("読み込み");
        setTodos((prev) => [...prev, newTodo]);
      } catch (error) {}
    } else {
      alert("empty item is invalid");
    }
  };

  const complete = async (id: string) => {
    await api.delete(`/api/todos/${id}`);
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  return (
    <>
      <div>Todo App</div>
      <List todos={todos} onComplete={complete} />
      <Form onSubmit={submit} />
    </>
  );
}
