import React, { useEffect, useState } from "react";
import api from "../axiosClient.js";
import { Todo } from "../type";

export default function useTodos() {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [editingId, setEditingId] = useState<string | null>(null);
  const [isLoading, setIsloading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  async function fetchTodos() {
    try {
      setIsloading(true);
      const results = await api.get(`/api/todos`);
      return results.data;
    } catch (error) {
      setError("Failed to fetch data");
      console.error("Failed to fetch data");
      return [];
    } finally {
      setIsloading(false);
    }
  }

  useEffect(() => {
    fetchTodos().then((data) => {
      setTodos(data);
    });
  }, []);

  const submit = async (formData: FormData) => {
    const todo = formData.get("todo");
    if (typeof todo === "string" && todo.trim() !== "") {
      try {
        setIsloading(true);
        const res = await api.post(`/api/todos`, { content: todo.trim() });
        setTodos((prev) => [...prev, res.data]);
        setIsloading(false);
      } catch (error) {
        setError("Failed to submit todo");
        console.error("Failed to submit todo");
      }
    } else {
      alert("empty item is invalid");
    }
  };

  const complete = async (id: string) => {
    const prevTodos = todos;
    try {
      await api.delete(`/api/todos/${id}`);
      const updatedTodos = todos.filter((todo) => todo.id !== id);
      setTodos(updatedTodos);
    } catch (error) {
      setError("Failed to complete todo");
      setTodos(prevTodos);
    }
  };

  const update = async (id: string, newContent: string) => {
    await api.put(`/api/todos/${id}`, { content: newContent });
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, content: newContent } : todo
      )
    );
    setEditingId(null);
  };

  return {
    submit,
    complete,
    update,
    isLoading,
    editingId,
    setEditingId,
    error,
    todos,
  };
}
